<?php // product_list.php // Connect to the MySQL database $conn = new mysqli('localhost', 'root', '', 'product_inventory'); // Retrieve all products from the database $result = $conn->query('SELECT * FROM products'); // Fetch the products as an associative array $products = $result->fetch_all(MYSQLI_ASSOC); // Close the database connection $result->close(); $conn->close(); ?> <!-- HTML table to display the product list --> <table> <thead> <tr> <th>Product Name</th> <th>Description</th> <th>Price</th> <th>Quantity in Stock</th> <th>Availability</th> <th>Actions</th> </tr> </thead> <tbody> <?php foreach ($products as $product): ?> <tr> <td><?php echo $product['name']; ?></td> <td><?php echo $product['description']; ?></td> <td><?php echo $product['price']; ?></td> <td><?php echo $product['quantity']; ?></td> <td><?php echo $product['available'] ? 'In Stock' : 'Out of Stock'; ?></td> <td> <a href="update_product.php?id=<?php echo $product['id']; ?>">Edit</a> <a href="delete_product.php?id=<?php echo $product['id']; ?>">Delete</a> </td> </tr> <?php endforeach; ?> </tbody> </table> ```